import React, { Component } from "react";
import "./App.css";

const sites = [
  { name: "Twitter" },
  { name: "VK"},
  { name: "OK"},
];

class WeatherDisplay extends Component {
  constructor() {
    super();
    this.state = {
      weatherData: null
    };
  }
  componentDidMount() {
    fetch("https://my-json-server.typicode.com/typicode/demo/db").then(res => res.json()).then(json => {
      this.setState({ weatherData: json });
    });
  }
  render() {
    const weatherData = this.state.weatherData;
    if (!weatherData) return <div>Loading</div>;
    const posts= weatherData.posts[0];
    return (
      <div>

        <p>ProfileName: {weatherData.profile.name}</p>
        <p>Comments: {weatherData.comments[0].body}</p>
      </div>
    );
  }
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      activePlace: 0
    };
  }
  render() {
    const activePlace = this.state.activePlace;
    return (
      <div className="App">
        {sites.map((place, index) => (
          <button
            key={index}
            onClick={() => {
              this.setState({ activePlace: index });
            }}
          >
            {place.name}
          </button>
        ))}
        <WeatherDisplay key={activePlace} zip={sites[activePlace].zip} />
      </div>
    );
  }
}

export default App;
