import React, { Component } from "react";
import "./App.css";

const sites = [
  { name: "Twitter" },
  { name: "San Jose"},
  { name: "Santa Cruz"},
  { name: "Honolulu"}
];

class WeatherDisplay extends Component {
  constructor() {
    super();
    this.state = {
      weatherData: null
    };
  }
  componentDidMount() {
    fetch("https://my-json-server.typicode.com/typicode/demo/db").then(res => res.json()).then(json => {
      this.setState({ weatherData: json });
    });
  }
  render() {
    const weatherData = this.state.weatherData;
    if (!weatherData) return <div>Loading</div>;
    const weather = weatherData.weather[0];
    return (
      <div>
        <h1>
        </h1>
        <p>ProfileName: {weatherData.profile.name}</p>
        <p>Comment: {weatherData.main.temp_max}°</p>
        <p>Low: {weatherData.main.temp_min}°</p>
        <p>Wind Speed: {weatherData.wind.speed} mi/hr</p>

      </div>
    );
  }
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      activePlace: 0
    };
  }
  render() {
    const activePlace = this.state.activePlace;
    return (
      <div className="App">
        {sites.map((place, index) => (
          <button
            key={index}
            onClick={() => {
              this.setState({ activePlace: index });
            }}
          >
            {place.name}
          </button>
        ))}
        <WeatherDisplay key={activePlace} zip={sites[activePlace].zip} />
      </div>
    );
  }
}

export default App;
