import React, { Component } from "react";
import "./App.css";

const SHOPS = [
  { name: "LC WAIKIKI", shop: "1" },
  { name: "H&M", shop: "2" },
  { name: "DEFACTO", shop: "3" },
];

class ShopDisplay extends Component {
  constructor() {
    super();
    this.state = {
      shopData: null
    };
  }
  componentDidMount() {
    fetch('https://my-json-server.typicode.com/typicode/demo/db').then(res => res.json()).then(json => {
      this.setState({ shopData: json });
    });
  }
  render() {
    const shopData = this.state.shopData;
    if (!shopData) return <div>Loading</div>;
    return (
      <div>
        <p>Jeans: {shopData.posts.id}</p>
        <p>Sweaters: {shopData.goods.sweaters.amount}</p>
        <p>T-Shirts: {shopData.goods.t_shirts.amount}</p>
      </div>
    );
  }
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      activeShop: 0
    };
  }
  render() {
    const activeShop = this.state.activeShop;
    return (
      <div className="App">
        {SHOPS.map((place, index) => (
          <button
            key={index}
            onClick={() => {
              this.setState({ activeShop: index });
            }}
          >
            {place.name}
          </button>
        ))}
        <ShopDisplay key={activeShop} id={SHOPS[activeShop].id} />
      </div>
    );
  }
}

export default App;
