import React, { Component } from "react";
import "./App.css";
//создаем массив названий для кнопок
const sites = [
  { name: "Twitter" },
  { name: "VK"},
  { name: "OK"},
];
//создаем компонент для отображения
class Display extends Component {
  constructor() {
    super();
    this.state = {
      Data: null
    };
  }
  //функция, которую вызовем после рендеринга, выполняем запрос к json-файлу
  componentDidMount() {
    fetch("https://my-json-server.typicode.com/typicode/demo/db").then(res => res.json()).then(json => {
      this.setState({ Data: json });
    });
  }
  //сам рендеринг
  render() {
    const Data = this.state.Data; // помещаем в коллекцию данный с json-файла
    if (!Data) return <div>Loading</div>; //див блок в качестве "загрузки" (когда бд недоступна либо загружается)
    return (
      <div>
        <p>ProfileName: {Data.profile.name}</p>
        <p>Comments: {Data.comments[0].body}</p>
      </div>
    );
  }
}
//компонент для 
class App extends Component {
  constructor() {
    super();
    this.state = {
      active: 0
    };
  }
  render() {
    const active = this.state.active;
    return (
      <div className="App">
        {sites.map((site, index) => (
          <button
            key={index}
            onClick={() => {
              this.setState({ active: index });
            }}
          >
            {site.name}
          </button>
        ))}
        <Display key={active} zip={sites[active]} />
      </div>
    );
  }
}

export default App;
